mapper.select:
select * from board order by num desc
limit #{skip},
#{count}

SELECT * from board
where boardFlag = #{boardFlag}

	and title like CONCAT('%',#{searchKey},'%')


	
		and addrNo = #{addrNo}
	

order by boardNo desc
limit #{skip}, #{count}

SELECT * from board
where boardFlag = #{boardFlag}

	and userIndex = #{userIndex}


	
		and addrNo = #{addrNo}
	

order by boardNo desc
limit #{skip}, #{count}

select count(*) from board
where boardFlag = #{boardFlag}

	or title like CONCAT('%',#{searchKey},'%')


	or userIndex = #{userIndex}


	
		or addrNo = #{addrNo}

select * from board
where boardFlag = #{boardFlag}

	and userIndex = #{userIndex}

order by boardNo desc
limit #{skip}, #{count}
